import React, {Component} from "react";

import moment from 'moment';

/* calculates time since upload in a human friendly format */
//const formatedTime = moment.duration(currentDate.diff(props.mangaChaptersUploadTime)).humanize();

class StudySession extends Component {

    constructor() {
        super();
        this.state = {
            session_numbers: 0,
            session_ongoing: 0,

            sessions: [
                // {
                //     session_id: 1,
                //     session_name: "Thanks for supporting FairManga ! ",
                //     session_start: moment(),
                //     session_end: ""
                // }
            ]
        }
    }
    // Creates Session and increments number of session and ongoing session will be true
    createSessionHandler = (e)=>{
        e.preventDefault()

        const new_session = {
            session_id: this.state.session_numbers,
            name: "",
            session_start: moment().format("DD-MM-YYYY hh:mm:ss"),
            session_end: ""
        }
        let sessions = [new_session, ...this.state.sessions]
        this.setState({sessions})
        let new_session_numbers = this.state.session_numbers + 1
        this.setState({
            session_numbers: new_session_numbers,
            session_ongoing: 1,
        })
    }

    stopSessionHandler = (e)=>{
        e.preventDefault()

        this.setState(prevState => ({
            sessions: {
                ...prevState.sessions,
                [prevState.sessions[this.state.session_numbers-1].session_end]: moment().format("DD-MM-YYYY hh:mm:ss"),
            },
        }));
        this.setState({
            session_ongoing: 0,
        })
        console.log(this.state)
    }


    // Deletes all mangas in the favorite State after clicking the delete button
    deleteSessionHandler = (e)=>{
        e.preventDefault()
        let sessions = []
        this.setState({sessions})
    }

    render() {
        let sessionsList = this.state.sessions.map(session=> {
            if(session.session_end != "")
            return(
                <div>
                    <h1>Session ID: {session.session_id}</h1>

                    <h1>Session Start: {session.session_start}</h1>

                    <h1>Session End: {session.session_end}</h1>
                </div>

            )
        })

        let currentSession = this.state.sessions.map(session=> {
            if(session.session_end == "")
                return(
                    <div key="{item}">
                        <h1>Session ID: {session.session_id}</h1>

                        <h1>Session Start: {session.session_start}</h1>

                        <h1>Session End: {session.session_end}</h1>
                    </div>

                )
        })

            return(
            <div>
                <h1>STUDY SESSION</h1>
                {this.state.session_ongoing != 1 ? <button onClick={this.createSessionHandler}>Create New Session</button>
                    : <button onClick={this.stopSessionHandler}>Stop Session</button>}{currentSession}


                <p>Past study sessions: </p>
                {sessionsList}
                <button>Delete all Sessions</button>

            </div>
        )

    }
}


export default StudySession;
