import React, {Component} from "react";

import Box from '@mui/material/Box';
import Slider from '@mui/material/Slider';
import RangeSlider from "./RangeSlider";


class Home extends Component {

    constructor() {
        super();
        this.state = {
            minTemp: 20,
            maxTemp: 37,
            set_break_every: 7,
        }
    }

    handleChangeTemp = (newValue) => {
        this.setState({
            minTemp: newValue[0],
            maxTemp: newValue[1],
        })
        console.log(this.state)
    };

    onInputchange = event => {
        event.preventDefault()
        console.log("event:",event.target.name)
        this.setState({
            [event.target.name]: event.target.value
        });
        console.log("on input change:",this.state)
    }

    render()
    {
        return (
            <div>
                <h1>Settings</h1>
                <h2>Minimum and Maximum Temperature:</h2>
                <div >
                    <p>Select the desired interval for your temperature in your room and get alarmed when it gets lower or higher:</p>
                    <div style={{
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        margin: 20 }}>
                        <RangeSlider
                            handleChangeTemp = {this.handleChangeTemp}
                        >
                        </RangeSlider>
                    </div>
                    <h2>Break After X Minutes:</h2>
                    <p>After how many minutes should you be reminded to take a break</p>
                    <input
                        type="number"
                        min={0}
                        max={1440}
                        //default = {200}
                        placeholder= {this.state.set_break_every}//"in minutes"
                        name="set_break_every"
                        onChange={this.onInputchange}
                        value={this.state.set_break_every}
                        required
                    />
                </div>
                <div></div>
                <button onClick={this.createSessionHandler}>Save Settings</button>


            </div>
        )
    }
}

export default Home;
