import logo from './logo.svg';
import './App.css';
import {BrowserRouter, Redirect, Route, Router, Switch} from "react-router-dom";
import Settings from './components/Settings.js'
import Nav from "./components/NavBar/Nav";
import StudySession from "./components/StudySession";
import Login from "./components/Login/Login";
import Signup from "./components/Signup/Signup"
import AuthVerify from "./components/common/auth-verify";
import Authservice from "./components/services/Authservice";
import {Component} from "react";
import ProtectedRoute from "./components/ProtectedRoute";

class App extends Component{
    constructor(props) {
        super(props);
        this.logOut = this.logOut.bind(this)
        this.state ={
            currentUser: undefined,
        }

    }
    logOut() {
        console.log("log out after time" )
        Authservice.logout();
        this.setState({
            currentUser: undefined,
        });
    }

    render(){
        //TODO: auth verify, to logout when jwt expires

        return (
            <div className="App">

                <BrowserRouter>
                    <Nav />
                    <Switch>

                        <ProtectedRoute exact path="/studysession" component={StudySession}/>
                        <Route exact path="/login" component={Login}/>
                        <Route exact path="/signup" component={Signup}/>
                        <ProtectedRoute exact path="/settings" component={Settings}/>
                        <ProtectedRoute path="*" component={StudySession}/>
                        <Route path="*" component={Login}/>

                    </Switch>
                    <AuthVerify logOut={this.logOut}/>

                </BrowserRouter>
            </div>
        );
    }

}

export default App;
