import React, {Component} from "react";

import moment from 'moment';
import axios from "axios";
import './StudySession.css';
import AreaRechartComponent from "./AreaRechartComponent";

const baseURL = "http://localhost:8080"


class StudySession extends Component {

    constructor() {
        super();
        this.state = {
            session_ongoing: 0, // Boolean: is there a current session ongoing? then do not show delete button

            sessions: [
                // {
                //     session_id: integer, // Every session has an unique ID
                //     session_name: string, // Name of the session
                //     session_start: date // date and time the session started
                //     session_end: date // date and time the session stopped
                //     session_breakTime: date // date and time when the next break is
                //     session_duration: date // date and time between start and end
                //     session_stopped: 0 // true if the session was stopped, false if it is still ongoing
                // }
            ],

            currentCO2: "500",
            currentTemp: "20",
            avgCO2:"550",
            avgTemp:"25",
        }
    }

    //get current co2 and temp measurements
    async componentDidMount() {
        await axios.get(baseURL+"/CO2/").then(async (res) => {
            let data = res.data.data
            console.log("CO2 data:", data)
            await this.setState({
                currentCO2: data
            })
        })

        await axios.get(baseURL+"/temp/").then((res) =>{
            let data = res.data.data
            console.log("Temperature data:",data)
            this.setState({currentTemp: data})
        })

        // Get all sessions from backend
        await axios.get(baseURL+"/session/").then((res) =>{
            let data = res.data
            console.log("Session data from backend get all sessions:",data)
            this.setState({sessions: data})

            // check if a session is ongoing
            if(this.state.sessions.length > 0){
                if (this.state.sessions[this.state.sessions.length-1].session_stopped===0){
                    this.setState({
                        session_ongoing: 1,
                    })
                }
                else{
                    this.setState({
                        session_ongoing: 0,
                    })
                }
            }
        })
        console.log("State after get all sessions: ", this.state.sessions)
    }

    // Creates Session and increments number of session and ongoing session will be true
    createSessionHandler = async (e)=>{
        e.preventDefault()

        let new_session = {
            session_id: this.state.sessions.length,
            session_name: "Test",
            session_stopped: 0,
            session_breakTime: 0,
        }

        // Send creation of session to backend and get the session_start of the backend
        console.log("This data will be sent to the backend: ", new_session)

        await axios.post(baseURL+'/session/', new_session, {})
             .then(resp => {
                 console.log("create Session respond:", resp)
                 new_session["session_start"] = resp["data"]["session_start"]
                 if (resp.status !== 200) {
                     throw new Error(`Request failed: ${resp.status}`);
                 }
             })
             .catch(err => {
                 console.log(err.response);
             })

        // Add the created session to the list of sessions
        let sessions = [new_session, ...this.state.sessions]
        this.setState({sessions})
        this.setState({
            session_ongoing: 1,
        })
        console.log("State after created session: ",this.state)
    }

    // Stop the current session and get the session_end time from the backend and save it in the last session
    stopSessionHandler = async (e)=>{
        e.preventDefault()
        let sessions
        await axios.post(baseURL+'/session/stop/', {})
            .then(resp => {
                 sessions = this.state.sessions.map(session => (
                    session.session_id===this.state.sessions.length-1 ? {...session, session_end: resp["data"]["session_end"], session_stopped: 1}: session
                ))
                console.log("sessions after stopped", this.state)

                if (resp.status !== 200) {
                    throw new Error(`Request failed: ${resp.status}`);
                }
            })
            .catch(err => {
                console.log(err.response);
            })

        await this.setState({ sessions });

        await this.setState({
            session_ongoing: 0,
        })
    }


    // Deletes all sessions after clicking the delete button
    deleteSessionHandler = (e)=>{
        e.preventDefault()
        let sessions = []
        this.setState({sessions})
        axios.delete(baseURL+'/session/', {})
            .then(resp => {
                if (resp.status !== 200) {
                    throw new Error(`Request failed: ${resp.status}`);
                }
            })
            .catch(err => {
                console.log(err.response);
            })
    }

    render() {
        // List of all sessions completed
        let sessionsList = this.state.sessions.map(session=> {
            if(session.session_stopped)
            return(
                <div key={session.session_id}>
                    <div className={"sessionElem"}>
                    <p><b>Session ID:</b> {session.session_id} | <b>Session Start:</b> {session.session_start} | <b>Session End:</b> {session.session_end}</p>
                    {/*| <b>Session Duartion:</b> {session.session_end - session.session_start)}</p>*/}
                        <AreaRechartComponent type={"Temperature"} data={[
                            {
                                "name": "18:10",
                                "Temperature": 24.1,
                            }]}></AreaRechartComponent>
                        <AreaRechartComponent type={"CO2"} data={[
                            {
                                "name": "18:10",
                                "CO2": 500,
                            }]}></AreaRechartComponent>
                    </div>
        </div>

            )
        })
        // List of current active session
        let currentSession = this.state.sessions.map(session=> {
            if(!session.session_stopped)
                return(
                    <div key={session.session_id}>
                        <p><b>Session ID:</b> {session.session_id} | <b>Session Start:</b> {session.session_start}</p>
                    </div>

                )
        })

            return(
                <div className={"back"}>
            <div className={"background"}>

                    <div className={"backgroundMeasurements"}>
                        <div className={"elem"}>
                            <h1>Current measurements</h1>
                            <h2>CO2</h2>
                            <p>{this.state.currentCO2} CO2e</p>
                            <h2>Temperature</h2>
                            <p>{this.state.currentTemp}° C</p>
                        </div>
                        <div className={"elem"}>
                            <h1>Average measurements</h1>
                            <h2>Average CO2</h2>
                            <p>{this.state.avgCO2} CO2e</p>
                            <h2>Average Temperature</h2>
                            <p>{this.state.avgTemp}° C</p>
                        </div>
                    </div>

                    <div className={"backgroundStudySession"}>
                        <div className={"elem"}>
                        <h1>STUDY SESSION</h1>
                        <h2>Current study sessions: </h2>
                        {this.state.session_ongoing !== 1 ? <button onClick={this.createSessionHandler}>Create New Session</button>
                            : <button onClick={this.stopSessionHandler}>Stop Session</button>}{currentSession}
                        <p></p>

                        {(this.state.sessions.length > 0) && (this.state.sessions[this.state.sessions.length-1].session_stopped!==0) && <h2>Past study sessions: </h2>}
                        {(this.state.sessions.length > 0) && (!this.state.session_ongoing) && (this.state.sessions[this.state.sessions.length-1].session_stopped!==0) && <button onClick={this.deleteSessionHandler}>Delete all Sessions</button>}

                        {sessionsList}
                    </div>
                        </div>
            </div>
                    </div>

    )

    }
}


export default StudySession;
