import React, {Component} from "react";

import Box from '@mui/material/Box';
import Slider from '@mui/material/Slider';
import RangeSlider from "./RangeSlider";


class Home extends Component {

    constructor() {
        super();
        this.state = {
            minTemp: 20,
            maxTemp: 37,
            set_break_every: 0,
        }
    }

    handleChangeTemp = (newValue) => {
        this.setState({
            minTemp: newValue[0],
            maxTemp: newValue[1],
        })
        console.log(this.state)
    };

    render()
    {
        return (
            <div>
                <h1>Settings</h1>
                <h2>Minimum and Maxmimum Temperature:</h2>
                <div >
                    <p>Select the desired interval for your temperature in your room and get alarmed when it gets lower or higher:</p>
                    <div style={{
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        margin: 20 }}>
                        <RangeSlider
                            handleChangeTemp = {this.handleChangeTemp}
                        >
                        </RangeSlider>
                    </div>
                </div>


            </div>
        )
    }
}

export default Home;
